import { __decorate } from "tslib";
import { Component } from '@angular/core';
let ManageBookComponent = class ManageBookComponent {
    constructor(databaseService) {
        this.databaseService = databaseService;
        this.displayedColumns = ['id', 'bookId', 'userId', 'borrowedDate', 'hasReturned'];
        this.totalBooksCount = 0;
        this.pageSize = 8;
        this.searchedInput = "";
        this.currentPageIndex = 0;
        this.role = "student";
    }
    async ngOnInit() {
        var data = await this.databaseService.getOrders().toPromise();
        this.orderList = data;
        var data2 = await this.databaseService.getBooks().toPromise();
        this.bookList = data2;
        var data3 = await this.databaseService.getUsers().toPromise();
        this.userList = data3;
        this.changeOrderFormat();
        console.log(this.orderList);
    }
    changeOrderFormat() {
        var orders;
        var books;
        var users;
        orders = this.orderList;
        books = this.bookList;
        users = this.userList;
        for (var i = 0; i < orders.length; i++) {
            for (var j = 0; j < books.length; j++) {
                if (orders[i].bookId == books[j].id) {
                    orders[i].bookId = books[j].bookName;
                }
            }
            for (var j = 0; j < users.length; j++) {
                if (orders[i].userId == users[j].id) {
                    orders[i].userId = users[j].userFirstName + " " + users[j].userLastName;
                }
            }
            console.log(orders[i].hasReturned);
            this.orders = orders;
            this.orderList = orders;
        }
    }
    changePage(event) {
        var tempOrders = this.orderList;
        this.orderList = tempOrders.slice(event.pageIndex * this.pageSize, (event.pageIndex + 1) * this.pageSize);
    }
    filterBooks(event) {
        this.searchedInput = event.target.value;
        var tempOrders = this.orderList;
        var tempOrders2 = [];
        for (var i = 0; i < tempOrders.length; i++) {
            if (tempOrders[i].bookId.includes(this.searchedInput) || tempOrders[i].userId.includes(this.searchedInput)) {
                tempOrders2.push(tempOrders[i]);
            }
        }
        this.orderList = tempOrders2;
        this.totalBooksCount = this.orderList.length;
        this.orderList = this.orderList.slice(this.currentPageIndex * this.pageSize, (this.currentPageIndex + 1) * this.pageSize);
    }
};
ManageBookComponent = __decorate([
    Component({
        selector: 'app-manage-book',
        templateUrl: './manage-book.component.html',
        styleUrls: ['./manage-book.component.scss']
    })
], ManageBookComponent);
export { ManageBookComponent };
