    constructor(databaseService) {
        this.databaseService = databaseService;
        this.displayedColumns = [
            'id',
            'bookId',
            'userId',
            'borrowedDate',
            'hasReturned',
        ];
        this.totalBooksCount = 0;
        this.pageSize = 10;
        this.searchedInput = '';
        this.currentPageIndex = 0;
        this.isLoggedIn = false;
        this.userId = sessionStorage.getItem('userId');
        this.role = sessionStorage.getItem('role');
        this.userName = sessionStorage.getItem('userName');
    }
    async ngOnInit() {
        if (this.userId != null) {
            this.isLoggedIn = true;
        }
        var data;
        if (this.role == 'student') {
            data = await this.databaseService
                .getOrdersByUserID(this.userName)
                .toPromise();
            this.orderList = data;
        }
        else {
            data = await this.databaseService.getAllOrders().toPromise();
            this.orderList = data;
        }
        var data2 = await this.databaseService.getBooks().toPromise();
        this.bookList = data2;
        var data3 = await this.databaseService.getUsers().toPromise();
        this.userList = data3;
        this.changeOrderFormat();
    }
    changeOrderFormat() {
        var orders;
        var books;
        var users;
        orders = this.orderList;
        books = this.bookList;
        users = this.userList;
        for (var i = 0; i < orders.length; i++) {
            for (var j = 0; j < books.length; j++) {
                if (orders[i].bookId == books[j].id) {
                    orders[i].bookId = books[j].bookName;
                }
            }
            for (var j = 0; j < users.length; j++) {
                if (orders[i].userId == users[j].id) {
                    orders[i].userId =
                        users[j].userFirstName + ' ' + users[j].userLastName;
                }
            }
            this.orders = orders;
            this.orderList = orders;
        }
    }
    changePage(event) {
        var tempOrders = this.orderList;
